document.addEventListener("DOMContentLoaded",(function(){document.querySelectorAll("nav a").forEach((t=>{t.addEventListener("click",(function(t){const e=this.getAttribute("href");if(e.startsWith("#")){t.preventDefault();const o=e.substring(1),n=document.getElementById(o);n?n.scrollIntoView({behavior:"smooth",block:"start"}):"#"===e&&window.scrollTo({top:0,behavior:"smooth"})}}))})),document.querySelectorAll('a[href="#"]').forEach((t=>{t.addEventListener("click",(function(t){t.preventDefault(),window.scrollTo({top:0,behavior:"smooth"})}))}))}));