document.addEventListener("DOMContentLoaded",(function(){const e=document.getElementById("theme-toggle-btn");if(!e)return;e.addEventListener("click",(function(){const e=document.body;e.classList.contains("theme-dark")?(e.classList.remove("theme-dark"),e.classList.add("theme-light"),localStorage.setItem("theme","light")):(e.classList.remove("theme-light"),e.classList.add("theme-dark"),localStorage.setItem("theme","dark"))}));"dark"===localStorage.getItem("theme")?(document.body.classList.add("theme-dark"),document.body.classList.remove("theme-light")):(document.body.classList.add("theme-light"),document.body.classList.remove("theme-dark"))}));